        // Create verification_questions table
        console.log('üìù Creating verification_questions table...');
        await sql`
            CREATE TABLE IF NOT EXISTS verification_questions (
                id SERIAL PRIMARY KEY,
                role VARCHAR(50) NOT NULL,
                step INTEGER NOT NULL DEFAULT 1,
                question_text_en TEXT NOT NULL,
                question_text_hi TEXT NOT NULL,
                question_text_mr TEXT,
                question_text_kn TEXT,
                question_text_te TEXT,
                question_text_or TEXT,
                options JSONB NOT NULL,
                points INTEGER DEFAULT 1,
                difficulty VARCHAR(20) DEFAULT 'medium',
                created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
            )
        `;
        console.log('‚úÖ verification_questions table created\n');

        // Create card_orders table
        console.log('üìù Creating card_orders table...');
        await sql`
            CREATE TABLE IF NOT EXISTS card_orders (
                id SERIAL PRIMARY KEY,
                user_id INTEGER NOT NULL REFERENCES users(id) ON DELETE CASCADE,
                full_name VARCHAR(255) NOT NULL,
                address TEXT NOT NULL,
                city VARCHAR(100) NOT NULL,
                pincode VARCHAR(10) NOT NULL,
                mobile VARCHAR(15) NOT NULL,
                status VARCHAR(50) DEFAULT 'pending',
                created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
                updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
            )
        `;
        console.log('‚úÖ card_orders table created\n');

        // Clear existing questions
        console.log('üóëÔ∏è Clearing existing questions...');
        await sql`DELETE FROM verification_questions`;

        // Insert questions
        console.log('üìù Inserting verification questions...');
        for (const q of questions) {
            await sql`
                INSERT INTO verification_questions (
                    role, step, question_text_en, question_text_hi, 
                    question_text_mr, question_text_kn, question_text_te, question_text_or,
                    options, points, difficulty
                )
                VALUES (
                    ${q.role}, ${q.step}, ${q.question_text_en}, ${q.question_text_hi}, 
                    ${q.question_text_mr || ''}, ${q.question_text_kn || ''}, ${q.question_text_te || ''}, ${q.question_text_or || ''},
                    ${q.options}::jsonb, ${q.points}, ${q.difficulty}
                )
            `;
        }
